}

.game-life-bar-part {

    height: 100%;
    box-shadow: 0 0 5px 0px #ee5253;
    background-color: #ee5253;
}

.game-life-bar {
    margin-top: 8px;
    background-color: #1e272e;
    box-shadow: 0 0 5px 0px #1e272e;
    width: 100%;
    height: 10px;
    border: 0px;
}

.game-timer-bar-part {
    height: 100%;
    box-shadow: 0 0 5px 0px #2e86de;
    background-color: #2e86de;
}

#game-next-turn-text {
    font-size: 5.0em;
    font-weight: normal;
    text-shadow: 2px 2px 10px black;
    color: white;
    -webkit-text-fill-color: white;
    -webkit-text-stroke-width: 0px;
    -webkit-text-stroke-color: white;
    white-space: nowrap;
}

#game-life-change-text {
    font-size: 4.5em;
    font-weight: normal;
    white-space: nowrap;
    text-shadow: 2px 2px 10px black;
    -webkit-text-stroke-width: 0px;
}

.game-life-change-good {
    color: #7bed9f;
    text-shadow: 2px 2px 10px black;
    -webkit-text-fill-color: #7bed9f;
    -webkit-text-stroke-width: 0px;
    -webkit-text-stroke-color: #7bed9f;
}

.game-life-change-bad {
    color: #ff6b81;
    text-shadow: 2px 2px 10px black;
    -webkit-text-fill-color: #ff6b81;
    -webkit-text-stroke-width: 0px;
    -webkit-text-stroke-color: #ff6b81;
}

#game-rps-second,
#game-rps-first {
    border: 0px solid;
    width: 100px;
    border-radius: 5px;
    margin: 5px;
    transition: 0.2s;
}
